import PropTypes from 'prop-types';

function ContentRowItem({ title, quantity, icon, color }) {
    return (
      <div className="col-md-4 mb-4">
        <div className={`card border-left-${color} shadow h-100 py-2`}>
          <div className="card-body">
            <div className="row no-gutters align-items-center">
              <div className="col mr-2">
                <div
                  className={`text-xs font-weight-bold text-${color} text-uppercase mb-1`}
                >
                  {title}
                </div>
                <div className="h5 mb-0 font-weight-bold text-gray-800">
                  {quantity}
                </div>
              </div>
              <div className="col-auto">
                <i className={`${icon} text-gray-300`}></i>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  ContentRowItem.defaultProps = {
    title: 'Sin titulo',
    quantity: 'Sin cantidad',
    icon: PropTypes.isRequired,
    color: PropTypes.string.isRequired
  };

  ContentRowItem.propTypes = {
    title: PropTypes.string.isRequired,
    quantity: PropTypes.number.isRequired,
    icon: PropTypes.isRequired,
    color: PropTypes.string.isRequired
  };
  
  export default ContentRowItem;
